import { signInWithPopup, TwitterAuthProvider } from "firebase/auth";
import { auth } from "../firebase";
import Button from "@mui/material/Button";

export default function Login() {
  const signInWithTwitter = () => {
    const provider = new TwitterAuthProvider();
    signInWithPopup(auth, provider)
      .then((userCredential) => {
        console.log("Logged in as:", userCredential.user);
        // Save the Twitter access token (for backend API calls)
        const credential = TwitterAuthProvider.credentialFromResult(userCredential);
        const token = credential.accessToken;
        localStorage.setItem("twitterToken", token);
      })
      .catch((error) => {
        console.error("Login failed:", error);
      });
  };

  return (
    <div>
      <Button variant="contained" onClick={signInWithTwitter}>
        Sign in with Twitter
      </Button>
    </div>
  );
}
